package com.alex.dashboarddemo.presentation.common.componentsimport androidx.compose.foundation.backgroundimport androidx.compose.foundation.clickableimport androidx.compose.foundation.layout.Boximport androidx.compose.foundation.layout.Rowimport androidx.compose.foundation.layout.fillMaxWidthimport androidx.compose.foundation.layout.paddingimport androidx.compose.foundation.shape.RoundedCornerShapeimport androidx.compose.material.Textimport androidx.compose.runtime.Composableimport androidx.compose.ui.Alignmentimport androidx.compose.ui.Modifierimport androidx.compose.ui.tooling.preview.Previewimport androidx.compose.ui.unit.dpimport com.alex.dashboarddemo.Rimport com.alex.dashboarddemo.presentation.common.model.GSDAButtonModel@Composablefun GSDAButton(    model: GSDAButtonModel,) {    Box(        modifier = Modifier            .fillMaxWidth()            .background(                color = model.backgroundColorBtn,                shape = RoundedCornerShape(80.dp),            )            .clickable {                model.onClick.invoke()            }.then(Modifier),    ) {        Row(            modifier = Modifier                .padding(start = 16.dp, end = 16.dp, top = 8.dp, bottom = 8.dp)                .align(Alignment.Center),        ) {            Text(                text = model.txtTitle,                color = model.colorTxt,                style = model.txtStyle,                modifier = Modifier.padding(start = 5.dp),            )        }    }}@Composable@Previewfun GSDAButtonPreview() {    GSDAButton(        GSDAButtonModel(            txtTitle = "ir a cuentas",            icon = R.drawable.close_eye,        ) {},    )}