package com.alex.dashboarddemo.presentation.common.componentsimport androidx.compose.foundation.layout.*import androidx.compose.foundation.shape.RoundedCornerShapeimport androidx.compose.material.Cardimport androidx.compose.material.Textimport androidx.compose.runtime.Composableimport androidx.compose.ui.Alignmentimport androidx.compose.ui.Modifierimport androidx.compose.ui.graphics.Colorimport androidx.compose.ui.text.font.FontWeightimport androidx.compose.ui.text.style.TextOverflowimport androidx.compose.ui.tooling.preview.Previewimport androidx.compose.ui.unit.dpimport androidx.compose.ui.unit.spimport com.alex.dashboarddemo.presentation.common.model.GSDAInformativeBadgeModelimport com.alex.dashboarddemo.ui.theme.GSVCAlert100import com.alex.dashboarddemo.ui.theme.GSVCAmountText100Centerimport com.alex.dashboarddemo.ui.theme.GSVCInfo100import com.alex.dashboarddemo.ui.theme.GSVCRedimport com.alex.dashboarddemo.ui.theme.GSVCSuccess100import com.alex.dashboarddemo.ui.theme.GSVCTitleBase100Leftimport com.alex.dashboarddemo.ui.theme.GSVCTitleText100Centerimport com.alex.dashboarddemo.ui.theme.h5import com.alex.dashboarddemo.ui.theme.subtitle1@Composablefun GSDAInformativeBadge(    badgesModel: GSDAInformativeBadgeModel,    alignment: Alignment = Alignment.TopStart,) {    Box(        modifier = Modifier.size(100.dp),        contentAlignment = alignment,    ) {        Card(            modifier = Modifier                .wrapContentSize()                .padding(0.dp),            shape = badgesModel.roundedCornerShape,            backgroundColor = badgesModel.backgroundBadgesStatus,        ) {            Text(                modifier = Modifier.padding(8.dp, 4.dp),                color = badgesModel.badgesTextColor,                style = badgesModel.textStyleBadgeStatus,                text = badgesModel.badgesStatusText,                maxLines = 1,                overflow = TextOverflow.Ellipsis,            )        }    }}@Preview(showBackground = true)@Composablefun GSDABadgesStatusPreviewAllDemo() {    Column(        modifier = Modifier.fillMaxSize(),        horizontalAlignment = Alignment.CenterHorizontally,        verticalArrangement = Arrangement.Center,    ) {        Text(            text = "Demo de Badges Status",            style = GSVCTitleBase100Left,            fontSize = 16.sp,            color = Color.Black,            fontWeight = FontWeight.Bold,        )        Spacer(modifier = Modifier.height(32.dp))        GSDAInformativeBadgeModel(            badgesStatusText = "Aceptada",            backgroundBadgesStatus = GSVCSuccess100,            textStyleBadgeStatus = subtitle1,            roundedCornerShape = RoundedCornerShape(0.dp, 8.dp, 0.dp, 8.dp),        ).apply { GSDAInformativeBadge(this) }        Spacer(modifier = Modifier.height(32.dp))        GSDAInformativeBadgeModel(            badgesStatusText = "Pendiente",            backgroundBadgesStatus = GSVCInfo100,            textStyleBadgeStatus = h5,            roundedCornerShape = RoundedCornerShape(8.dp, 0.dp, 8.dp, 0.dp),        ).apply { GSDAInformativeBadge(this) }        Spacer(modifier = Modifier.height(32.dp))        GSDAInformativeBadgeModel(            badgesStatusText = "Status Informativo ",            backgroundBadgesStatus = GSVCAlert100,            textStyleBadgeStatus = GSVCTitleText100Center,            roundedCornerShape = RoundedCornerShape(16.dp, 8.dp, 32.dp, 0.dp),        ).apply { GSDAInformativeBadge(this) }        Spacer(modifier = Modifier.height(32.dp))        GSDAInformativeBadgeModel(            badgesStatusText = "Status Informativo",            backgroundBadgesStatus = GSVCRed,            textStyleBadgeStatus = GSVCAmountText100Center,            roundedCornerShape = RoundedCornerShape(64.dp, 8.dp, 0.dp, 64.dp),        ).apply { GSDAInformativeBadge(this) }    }}