package com.alex.dashboarddemo.presentation.dashboardimport android.content.Contextimport com.alex.dashboarddemo.Rfun getIconAsDrawable(mContext: Context, drawableStr: String) =    try {        mContext.resources.getIdentifier(            drawableStr,            "drawable",            mContext.packageName,        )    } catch (e: IllegalArgumentException) {        R.drawable.gsvc_ic_logo_baz    }